import React, { Component } from "react"
import InputPage from "./../input/MDBInput"
import TextareaPage from "./../input/MDBText"
import FooterPage from "../../partial/components/footer"
import FixedNavbarExample from "../../partial/components/navbar"
import axios from "axios";


class  AddUpdate extends Component{

    constructor(props){
        super(props);
        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeUrl = this.onChangeUrl.bind(this);
        this.onChangeDesc = this.onChangeDesc.bind(this);

        this.state={
            title:"",
            imageUrl:"",
            description:""
        }
    }

    onChangeTitle(e){
        this.setState({
            title:e.target.value
        })
    }
    onChangeUrl(e){
        this.setState({
            imageUrl:e.target.value
        })
    }
    onChangeDesc(e){
        this.setState({
            description:e.target.value
        })
    }

    state={
        // blog:{
        //     title,
        //     imageUrl,
        //     description
        // }
    }


    componentDidMount(){

    }
    addBlog = (e) =>{

        const title = this.getTitle.value;
        const imageUrl = this.getUrl.value;
        const  description = this.getText.value;
        const blog = {
            "title":title,
            "imageUrl":imageUrl,
            "description":description
        }
        axios.post('api/blogs',blog)
        .then(res => {
            if(res.data){
                this.props.getBlogs();
                this.setState({blog:""})
            }
        })
        .catch(err => console.log(err))

    }



    render(){
        return(
            <div>
                <FixedNavbarExample />
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-lg-4 col-12 contact-us justify-content-center">
                        <form>
                            <h1>Add Project</h1>
                            <InputPage  label="Project Name" type="text"/>
                            <InputPage  label="Image URL" type="url"/>
                            <TextareaPage  />
                            <div className="text-center">
                                <button className="btn btn-default" onClick={this.addBlog}>Submit</button>
                            </div>
                        </form>
                        </div>
                    </div>
                </div>
                <FooterPage />
            </div>
        )    
    }
}

