import React, { Component } from "react"
import InputPage from "./../input/MDBInput"
import TextareaPage from "./../input/MDBText"
import FooterPage from "../../partial/components/footer"
import FixedNavbarExample from "../../partial/components/navbar"
import Axios from "axios";


class  AddUpdate extends Component{

    state={
        action:""
    }


    componentDidMount(){

    }
    addBlog = () =>{
        Axios.post('api/blogs')
    }

    handleChange = (e) =>  {
        this.setState({
            action: e.target.value
        })
    }

    render(){
        return(
            <div>
                <FixedNavbarExample />
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-lg-4 col-12 contact-us justify-content-center">
                        <form>
                            <h1>Add Project</h1>
                            <InputPage onChange={this.handleChange} label="Project Name" type="text"/>
                            <InputPage onChange={this.handleChange} label="Image URL" type="url"/>
                            <TextareaPage onChange={this.handleChange}/>
                            <div className="text-center">
                                <button className="btn btn-default">Submit</button>
                            </div>
                        </form>
                        </div>
                    </div>
                </div>
                <FooterPage />
            </div>
        )    
    }
}

