import React, { Component } from "react"
import InputPage from "./../input/MDBInput"
import TextareaPage from "./../input/MDBText"
import FooterPage from "../../partial/components/footer"
import FixedNavbarExample from "../../partial/components/navbar"
import axios from "axios";


class  AddUpdate extends Component{

    state={
        // blog:{
        //     title,
        //     imageUrl,
        //     description
        // }
    }


    componentDidMount(){

    }
    addBlog = (e) =>{

        const title = this.getTitle.value;
        const imageUrl = this.getUrl.value;
        const  description = this.getText.value;
        blog = {
            "title":title,
            "imageUrl":imageUrl,
            "description":description
        }
        axios.post('api/blogs',blog)
        .then(res => {
            if(res.data){
                this.props.getBlogs();
                this.setState({blog:""})
            }
        })
        .catch(err => console.log(err))

    }



    render(){
        return(
            <div>
                <FixedNavbarExample />
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-lg-4 col-12 contact-us justify-content-center">
                        <form>
                            <h1>Add Project</h1>
                            <InputPage ref={(input)=>this.getTitle = input} label="Project Name" type="text"/>
                            <InputPage ref={(input)=>this.getUrl = input} label="Image URL" type="url"/>
                            <TextareaPage ref={(input)=>this.getText = input} />
                            <div className="text-center">
                                <button className="btn btn-default" onClick={this.addBlog}>Submit</button>
                            </div>
                        </form>
                        </div>
                    </div>
                </div>
                <FooterPage />
            </div>
        )    
    }
}

