import React,{ Component } from "react";
import FooterPage from "../../partial/components/footer"
import FixedNavbarExample from "../../partial/components/navbar"
import { MDBInput } from "mdbreact";
import Axios from "axios";

export default class UpdateBlog extends Component{

    state={
        blog : ""
    }
    componentDidMount(props){
        Axios.get(`/api/blogs/${this.props.location.state.id}`)
        .then(res => {
            if(res.data){
                this.setState=({
                    blog:res.data
                })
            }
        })

    }
    updateBlog(){
        Axios.post('/api/blogs/update/:')
    }
    render(){
        let  {blog} = this.state.blog;
        return(
            <div>
                <FixedNavbarExample />
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-lg-4 col-12 contact-us justify-content-center">
                        <form onSubmit={this.onSubmit}>
                            <h1>Add Update</h1>
                            <MDBInput  onInput={this.onChangeTitle} label="Project Name" value={blog.title} type="text"/>
                            <MDBInput  onInput={this.onChangeUrl} label="Image URL" value={blog.imageUrl} type="url"/>
                            <MDBInput type="textarea" value={blog.description} onInput={this.onChangeDesc} />
                            <div className="text-center  form-group">
                                <button type="submit" className="btn btn-default" >Submit</button>
                            </div>
                        </form>
                        </div>
                    </div>
                </div>
                <FooterPage />
            </div>
        )    
    }
}
