import React, { Component } from "react"
import InputPage from "./../input/MDBInput"
import TextareaPage from "./../input/MDBText"
import FooterPage from "../../partial/components/footer"
import FixedNavbarExample from "../../partial/components/navbar"
import axios from "axios";


class  AddUpdate extends Component{

    constructor(props){
        super(props);
        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeUrl = this.onChangeUrl.bind(this);
        this.onChangeDesc = this.onChangeDesc.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state={
            title:"",
            imageUrl:"",
            description:""
        }
        self = this;
    }

    onChangeTitle(e){
        console.log('here');
        alert('hello');
        this.setState({
            title:e.target.value
        })
    }
    onChangeUrl(e){
        this.setState({
            imageUrl:e.target.value
        })
    }
    onChangeDesc(e){
        this.setState({
            description:e.target.value
        })
    }

    onSubmit (e) {
        e.prevenDefault();
        console.log('here');
        const blog = {
            title:this.state.title,
            imageUrl:this.state.imageUrl,
            description:this.state.description
        }
        axios.post('/api/blogs', blog)
        .then(res => console.log(res.data));

        // this.setState({
        //     title:"",
        //     imageUrl:"",
        //     description:""
        // })

    }



    render(){
        return(
            <div>
                <FixedNavbarExample />
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-lg-4 col-12 contact-us justify-content-center">
                        <form onSubmit={this.onSubmit}>
                            <h1>{this.state.title}</h1>
                            <InputPage  onChange={this.onChangeTitle} label="Project Name" type="text"/>
                            <InputPage  onChange={this.onChangeUrl} label="Image URL" type="url"/>
                            <TextareaPage  onChange={this.onChangeDesc} />
                            <div className="text-center">
                                <button type="submit" className="btn btn-default" >Submit</button>
                            </div>
                        </form>
                        </div>
                    </div>
                </div>
                <FooterPage />
            </div>
        )    
    }
}

